mapper.select:
select
user_id, user_name, user_password, user_email, user_nickname, user_profile_photo
from blog.xz_users


        and user_id = #{userId}


        and user_name = #{userName}


        and user_password = #{userPassword}


        and user_email = #{userEmail}


        and user_nickname = #{userNickname}


        and user_profile_photo = #{userProfilePhoto}
